package com.hxr.javatone.Sort;/** * Created with IntelliJ IDEA. * Project: test-jar * Author: Kevin * Date: 16/6/13 * Time: 下午9:12 */public class QuickSort {	public static void main(String[] args) {		int[] a = { 26, 5, 98, 108, 28, 99, 100, 56, 34, 1 };		printArray("排序前：", a);		QuickSort(a, 0, a.length - 1);		printArray("排序后：", a);	}	private static void printArray(String pre, int[] a) {		System.out.print(pre + "\n");		for (int i = 0; i < a.length; i++)			System.out.print(a[i] + "\t");		System.out.println();	}	private static void QuickSort(int[] a, int left, int right) {		if (left >= right)			return;		int temp = a[left];		int i = left;		int j = right;		while (i<j){			while (a[j] >= temp && i < j) {				j--;			}			while (a[i] <= temp && i < j) {				i++;			}			if(i<j){				int s = a[i];				a[i] = a[j];				a[j] = s;			}		}				a[left] = a[i];		a[i] = temp;				QuickSort(a, left, i - 1);		QuickSort(a, i + 1, right);	}}