package com.hxr.javatone.gc;import java.util.WeakHashMap;/** * Created with IntelliJ IDEA. * Project: test-jar * Author: Kevin * Date: 2017/11/25 * Time: 下午9:24 */public class WeakHashMapTest {	public static void main(String[] args) {		WeakHashMap<BigDateKey, BigDateValue> cache = new WeakHashMap<BigDateKey, BigDateValue>();		BigDateValue content = new BigDateValue("content");		BigDateKey key1 = new BigDateKey("key1");		cache.put(key1, content);		System.out.println((cache.containsKey(key1)));		key1 = null;		System.gc();		System.out.println((cache.containsKey(key1)));	}	static class BigDateKey{		private String keyName;		public BigDateKey(String keyName) {			this.keyName = keyName;		}	}	static class BigDateValue{		private  String content;		public BigDateValue(String content) {			this.content = content;		}	}}