mapper.select:
SELECT

FROM sys_user a
WHERE a.id = #{id}

SELECT

FROM sys_user a
WHERE a.login_name = #{loginName}

mapper.update:
UPDATE sys_user SET 
	name = #{name}, 
	login_ip = #{loginIp},
	login_date = #{loginDate}
WHERE id = #{id}

UPDATE sys_user SET 
	login_ip = #{loginIp}, 
	login_Date = #{loginDate} 
WHERE id = #{id}
